const getClassesBodies = (txt, arr) => {
    let result = [], classBody = '', findIndex = 0, startBracket = 0, endBracket = 0, i = 0;
    
        while( i < arr.length ){
            
            findIndex = txt.indexOf(arr[i], findIndex < 1 ? findIndex : findIndex+1 );
          
            if(findIndex > -1){
                startBracket = txt.indexOf('{', findIndex);
                endBracket = txt.indexOf('}', findIndex);
                classBody = txt.slice(startBracket, endBracket+1);

                result.push(arr[i] + classBody);

                classBody = '';
                startBracket = 0;
                endBracket = 0;
            }
            else{
              findIndex = 0;
              i += 1;
            }

        }

    return JSON.stringify(result);
}

